import matplotlib.pyplot as plot
def bubbleSort(k, size):
    for i in range(size):
        for j in range (i, size):
            if (k[i]>=k[j]):
                k[i],k[j] = k[j],k[i]

def ScatterPlot(k):
    xaxis = []
    for i in k:
        xaxis.append(k.index(i))
    yaxis = []
    for i in k:
        yaxis.append(len(i))
    plot.scatter(xaxis,yaxis)
    plot.xlabel('order')
    plot.ylabel('length of string')
    plot.show(block="false")

